SET `status` = 'ok'
WHERE `id_company` = 1 AND `access_key` = '22345678901234567890123456789012345678901234';

UPDATE `documents`
SET `status` = 'ok'
WHERE `id_company` = 3 AND `access_key` = '62345678901234567890123456789012345678901234';

UPDATE `documents`
SET `status` = 'ok'
WHERE `id_company` = 5 AND `access_key` = '00000000000000000000000000000000000000000000';


